DROP TABLE IF EXISTS `setting`;

CREATE TABLE `setting` (
  `id` tinyint(3) unsigned NOT NULL AUTO_INCREMENT COMMENT '站点id',
  `site` varchar(50) NOT NULL DEFAULT '' COMMENT '站点名称',
  `project` varchar(10) NOT NULL COMMENT '站点英文名称',
